import React from "react";
import './index.css'
import { useFormik } from 'formik';
// TODO: import useFormik from formik library

function App() {
  // TODO: add a const called formik assigned to useFormik()
  let loginMsg = '';

  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
    },

    // onSubmit: values => {
    //   console.log(`form: ${values}`)
    // },

    onSubmit: values => {
      loginMsg = `form: ${values}`;
    },

    validate: values => {
      let errors = {
        email: '',
        password: ''
      };

      if (!values.email) {
        errors.email = 'Field Required';
      } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        errors.email = 'Username should be an email';
      }

      if(!values.password) errors.password = "Field Required";
      return errors
    }
  });

  return (
    <div>
      <div className="login-form">
        <h1>LOGIN</h1>
        <form>
          <div>Email</div>
          <input id='email' type="text" onChange={formik.handleChange} value={formik.values.email} />
            <div>
              {formik.errors.email ? <div style={{color: 'red'}}>{formik.errors.email}</div> : null}
            </div>

          <div>Password</div>
          <input id='password' type="text" onChange={formik.handleChange} value={formik.values.password} />
            <div>
              {formik.errors.password ? <div style={{color: 'red'}}>{formik.errors.password}</div> : <div style={{color: 'green'}}>{formik.errors.password}</div>}
            </div>

          <div>
            <button id="submitBtn" type='submit'>Submit</button>
          </div>

          <div>
            <h1>{loginMsg}</h1>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
